import React from "react";

function MessageInput({ messageText, handleInputChange, handleFormSubmit }) {
  return (
    <form>
      <div className="form-group">
        <label htmlFor="Query">
          <strong>New Message</strong>
        </label>
        <input
          className="form-control"
          id="new-message"
          type="text"
          value={messageText}
          name="message-text"
          onChange={handleInputChange}
          required
        />
      </div>
      <div>
        <button
          onClick={handleFormSubmit}
          type="submit"
          className="btn btn-lg btn-danger float-right"
        >
          Submit
        </button>
      </div>
    </form>
  );
}

export default Form;
